import React, { useState, useEffect } from 'react';
import { Plus, Edit, Trash2, BarChart3, FileText, Users, Calendar, Award } from 'lucide-react';
import axios from 'axios';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('projects');
  const [projects, setProjects] = useState([]);
  const [achievements, setAchievements] = useState([]);
  const [stats, setStats] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editingProject, setEditingProject] = useState(null);
  const [editingAchievement, setEditingAchievement] = useState(null);
  const [loading, setLoading] = useState(true);

  const [formData, setFormData] = useState({
    title: '',
    authors: '',
    adviser: '',
    year: new Date().getFullYear(),
    abstract: '',
    keywords: '',
    department: 'IE',
    project_type: 'MOR',
    status: 'completed',
    pdf: null
  });

  const [achievementFormData, setAchievementFormData] = useState({
    title: '',
    description: '',
    image: null
  });

  useEffect(() => {
    fetchProjects();
    fetchAchievements();
    fetchStats();
  }, []);

  const fetchAchievements = async () => {
    try {
      const response = await axios.get('/api/achievements');
      setAchievements(response.data);
    } catch (error) {
      console.error('Error fetching achievements:', error);
    }
  };

  const fetchProjects = async () => {
    try {
      const response = await axios.get('/api/projects');
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get('/api/projects/stats/overview');
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.keys(formData).forEach(key => {
      if (key === 'pdf' && formData[key]) {
        data.append('pdf', formData[key]);
      } else if (key !== 'pdf') {
        data.append(key, formData[key]);
      }
    });

    try {
      if (editingProject) {
        await axios.put(`/api/projects/${editingProject.id}`, data);
      } else {
        await axios.post('/api/projects', data);
      }

      setShowModal(false);
      setEditingProject(null);
      resetForm();
      await fetchProjects();
      await fetchStats();

      // Trigger a custom event to update home page stats
      window.dispatchEvent(new CustomEvent('projectsUpdated'));
    } catch (error) {
      console.error('Error saving project:', error);
      alert('Error saving project. Please try again.');
    }
  };

  const handleEdit = (project) => {
    setEditingProject(project);
    setFormData({
      title: project.title,
      authors: project.authors,
      adviser: project.adviser || '',
      year: project.year,
      abstract: project.abstract || '',
      keywords: project.keywords || '',
      department: project.department,
      project_type: project.project_type,
      status: project.status,
      pdf: null
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this project?')) {
      try {
        await axios.delete(`/api/projects/${id}`);
        await fetchProjects();
        await fetchStats();

        // Trigger a custom event to update home page stats
        window.dispatchEvent(new CustomEvent('projectsUpdated'));
      } catch (error) {
        console.error('Error deleting project:', error);
        alert('Error deleting project. Please try again.');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      authors: '',
      adviser: '',
      year: new Date().getFullYear(),
      abstract: '',
      keywords: '',
      department: 'IE',
      project_type: 'MOR',
      status: 'completed',
      pdf: null
    });
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  if (loading) {
    return (
      <div style={{ padding: '2rem 0', minHeight: '80vh' }}>
        <div className="container">
          <div style={{ textAlign: 'center', padding: '3rem' }}>
            <div style={{ fontSize: '1.2rem', color: '#64748b' }}>Loading dashboard...</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="container">
        {/* Header */}
        <div className="dashboard-header">
          <div>
            <h1 style={{ fontSize: '2rem', fontWeight: '600', color: '#1e3a8a' }}>
              Admin Dashboard
            </h1>
            <p style={{ color: '#64748b' }}>Manage repository projects and content</p>
          </div>
          <button
            onClick={() => {
              resetForm();
              setEditingProject(null);
              setShowModal(true);
            }}
            className="btn btn-primary"
            style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
          >
            <Plus size={20} />
            Add Project
          </button>
        </div>

        {/* Statistics */}
        {stats && (
          <div className="stats-grid">
            <div className="stat-card">
              <FileText size={32} style={{ color: '#f97316', marginBottom: '0.5rem' }} />
              <div className="stat-number">{stats.total}</div>
              <div className="stat-label">Total Projects</div>
            </div>

            <div className="stat-card">
              <Users size={32} style={{ color: '#1e3a8a', marginBottom: '0.5rem' }} />
              <div className="stat-number">
                {stats.byDepartment.find(d => d.department === 'IE')?.count || 0}
              </div>
              <div className="stat-label">IE Projects</div>
            </div>

            <div className="stat-card">
              <BarChart3 size={32} style={{ color: '#f97316', marginBottom: '0.5rem' }} />
              <div className="stat-number">
                {stats.byDepartment.find(d => d.department === 'CPE')?.count || 0}
              </div>
              <div className="stat-label">CPE Projects</div>
            </div>

            <div className="stat-card">
              <Calendar size={32} style={{ color: '#1e3a8a', marginBottom: '0.5rem' }} />
              <div className="stat-number">
                {stats.byYear[0]?.year || new Date().getFullYear()}
              </div>
              <div className="stat-label">Latest Year</div>
            </div>
          </div>
        )}

        {/* Projects Table */}
        <div style={{ 
          background: 'white', 
          borderRadius: '12px', 
          overflow: 'hidden',
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)'
        }}>
          <div style={{ 
            padding: '1.5rem', 
            borderBottom: '1px solid #e2e8f0',
            background: '#f8fafc'
          }}>
            <h2 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1e3a8a' }}>
              All Projects ({projects.length})
            </h2>
          </div>

          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr style={{ background: '#f8fafc' }}>
                  <th style={{ padding: '1rem', textAlign: 'left', fontWeight: '600', color: '#1e3a8a' }}>
                    Title
                  </th>
                  <th style={{ padding: '1rem', textAlign: 'left', fontWeight: '600', color: '#1e3a8a' }}>
                    Authors
                  </th>
                  <th style={{ padding: '1rem', textAlign: 'left', fontWeight: '600', color: '#1e3a8a' }}>
                    Department
                  </th>
                  <th style={{ padding: '1rem', textAlign: 'left', fontWeight: '600', color: '#1e3a8a' }}>
                    Type
                  </th>
                  <th style={{ padding: '1rem', textAlign: 'left', fontWeight: '600', color: '#1e3a8a' }}>
                    Year
                  </th>
                  <th style={{ padding: '1rem', textAlign: 'left', fontWeight: '600', color: '#1e3a8a' }}>
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody>
                {projects.map(project => (
                  <tr key={project.id} style={{ borderBottom: '1px solid #e2e8f0' }}>
                    <td style={{ padding: '1rem' }}>
                      <div style={{ fontWeight: '500', color: '#1e3a8a' }}>
                        {project.title}
                      </div>
                    </td>
                    <td style={{ padding: '1rem', color: '#64748b' }}>
                      {project.authors}
                    </td>
                    <td style={{ padding: '1rem' }}>
                      <span style={{
                        background: project.department === 'IE' ? '#1e3a8a' : '#f97316',
                        color: 'white',
                        padding: '0.25rem 0.5rem',
                        borderRadius: '4px',
                        fontSize: '0.8rem',
                        fontWeight: '500'
                      }}>
                        {project.department}
                      </span>
                    </td>
                    <td style={{ padding: '1rem', color: '#64748b' }}>
                      {project.project_type}
                    </td>
                    <td style={{ padding: '1rem', color: '#64748b' }}>
                      {project.year}
                    </td>
                    <td style={{ padding: '1rem' }}>
                      <div style={{ display: 'flex', gap: '0.5rem' }}>
                        <button
                          onClick={() => handleEdit(project)}
                          style={{
                            background: 'none',
                            border: 'none',
                            color: '#1e3a8a',
                            cursor: 'pointer',
                            padding: '0.5rem'
                          }}
                        >
                          <Edit size={16} />
                        </button>
                        <button
                          onClick={() => handleDelete(project.id)}
                          style={{
                            background: 'none',
                            border: 'none',
                            color: '#dc2626',
                            cursor: 'pointer',
                            padding: '0.5rem'
                          }}
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2 className="modal-title">
                {editingProject ? 'Edit Project' : 'Add New Project'}
              </h2>
              <button
                onClick={() => setShowModal(false)}
                className="close-btn"
              >

              </button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Title *</label>
                <input
                  type="text"
                  name="title"
                  className="form-input"
                  value={formData.title}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Authors *</label>
                <input
                  type="text"
                  name="authors"
                  className="form-input"
                  value={formData.authors}
                  onChange={handleChange}
                  placeholder="Separate multiple authors with commas"
                  required
                />
              </div>

              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
                <div className="form-group">
                  <label className="form-label">Department *</label>
                  <select
                    name="department"
                    className="form-select"
                    value={formData.department}
                    onChange={handleChange}
                    required
                  >
                    <option value="IE">Industrial Engineering</option>
                    <option value="CPE">Computer Engineering</option>
                  </select>
                </div>

                <div className="form-group">
                  <label className="form-label">Project Type *</label>
                  <select
                    name="project_type"
                    className="form-select"
                    value={formData.project_type}
                    onChange={handleChange}
                    required
                  >
                    <option value="MOR">Methods of Research</option>
                    <option value="Capstone">Capstone Project</option>
                    <option value="Design Project">Design Project</option>
                  </select>
                </div>
              </div>

              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
                <div className="form-group">
                  <label className="form-label">Year *</label>
                  <input
                    type="number"
                    name="year"
                    className="form-input"
                    value={formData.year}
                    onChange={handleChange}
                    min="2019"
                    max={new Date().getFullYear() + 1}
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Status</label>
                  <select
                    name="status"
                    className="form-select"
                    value={formData.status}
                    onChange={handleChange}
                  >
                    <option value="completed">Completed</option>
                    <option value="ongoing">Ongoing</option>
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Adviser</label>
                <input
                  type="text"
                  name="adviser"
                  className="form-input"
                  value={formData.adviser}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label className="form-label">Abstract</label>
                <textarea
                  name="abstract"
                  className="form-textarea"
                  value={formData.abstract}
                  onChange={handleChange}
                  rows="4"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Keywords</label>
                <input
                  type="text"
                  name="keywords"
                  className="form-input"
                  value={formData.keywords}
                  onChange={handleChange}
                  placeholder="Separate keywords with commas"
                />
              </div>

              <div className="form-group">
                <label className="form-label">PDF Document</label>
                <input
                  type="file"
                  name="pdf"
                  className="form-input"
                  onChange={handleChange}
                  accept=".pdf"
                />
                {editingProject && editingProject.pdf_filename && (
                  <p style={{ fontSize: '0.9rem', color: '#64748b', marginTop: '0.5rem' }}>
                    Current file: {editingProject.pdf_filename}
                  </p>
                )}
              </div>

              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end' }}>
                <button
                  type="button"
                  onClick={() => setShowModal(false)}
                  className="btn btn-secondary"
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  {editingProject ? 'Update Project' : 'Add Project'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
